package input_output;

import java.util.Scanner;

public class IO_1008 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Scanner scan = new Scanner(System.in);
		double a, b;
		a = scan.nextDouble();
		b = scan.nextDouble();
		System.out.println(a/b);
		scan.close();
	}

}
